mesajlar VALUES (817, '13', '1', 1095018593, 'r', 'emrah denen üyeliği ben denemek için aldım özür dilerim. üyeliğin onaylanmaya ihtiyacı var mı diye merak etmiştim. silebilirsin.');INSERT INTO ob_sinsi VALUES (11, 'entrilere girilen yorumlari emayil ilen haber verme fasilitesi eklendi, pek nacizane oldu.', 1069538400);INSERT INTO ob_sinsi VALUES (3, 'popup sorunlari cozuldu, eski anketlerin sonuclari gozukur oldu, su oldu bu oldu.', 1069192800);INSERT INTO ob_sinsi VALUES (4, 'en bi enler istatistik fasilitesi eklendi oy aman oy!', 1069279200);INSERT INTO ob_sinsi VALUES (5, 'oyoyoy fasilitesi eklendi, istatistik bolumu guncellendi.', 1069279200);INSERT INTO ob_sinsi VALUES (6, 'bakiniz dugmeleri eklendi, pek bi hos oldu, tey tey teey...', 1069452000);INSERT INTO ob_sinsi VALUES (7, 'sinsi huseyin artik guncellenince haber eder oldu, veritabani moduna gecti!', 1069538400);INSERT INTO ob_sinsi VALUES (10, 'degistirecem ben bunu ozelligi artik sadece kendi entrilerinizde gorunur oldu.', 1069538400);INSERT INTO ob_sinsi VALUES (12, 'istatistik bolumu guncellendi, artik populer ve gariban entrileri/yorumlari duzgun gosteriyor.', 1069538400);INSERT INTO ob_sinsi VALUES (13, 'renkli portakalimiz geri dondu, bir daha hic kararmamasi dilegiyle...', 1069538400);INSERT INTO ob_sinsi VALUES (14, 'istatistik fasilitesindeki secenek sorunu duzeltildi, secmece karpuz gibi oldu hepsi.', 1069624800);INSERT INTO ob_sinsi VALUES (15, 'emayil ile haber verme fasilitelerindeki puruzler duzeltildi, ama duzeltene kadar canim cikti, orasi ayri.', 1069624800);INSERT INTO ob_sinsi VALUES (16, 'yorumlar icin sil gitsin fasilitesi eklendi. ancak entriler icin henuz eklenemiyor, neden derseniz entrileri silince yorumlar da siliniyor, olmuyor, olamiyor.', 1069624800);INSERT INTO ob_sinsi VALUES (17, 'yeni entri girerken baslikta yanlislikla enter\'a basma durumunda entrinin daha tamamlanmadan girilmesi sorunu cozuldu (bu nasil bir cumle oldu ben de cozemedim, ama anlayin iste).', 1069711200);INSERT INTO ob_sinsi VALUES (18, 'haftanin dedikodusu fasilitesi icin dedikodularinizi beklemekteyim, lutfen email ile veya chat yoluyla gonderiniz. dedikodularin kisa olmamasi ("su soyleymis" gibi), biraz daha uzun uzun anlatilmis olmasi gerekmektedir, ne de olsa bir hafta boyunca kosede kalacaklar, unutmayin.', 1069711200);INSERT INTO ob_sinsi VALUES (19, 'haftanin dedikodularinin kisa kisa birkac dedikodu olmasina karar verildi, ayrica dedikoducular secildi (kendilerini biliyorlar), fasilite tamamlandi, pek yakinda acilisi yapicaz.', 1069711200);INSERT INTO ob_sinsi VALUES (20, 'haftanin dedikodulari fasilitesi sisteme eklenmistir, vatana millete hayirli olsun. ayrica birbirinize rumuzlarinizi soylemeyin, kimin kim oldugunun bilinmemesi daha hos olacaktir. o kadar rumuz sistemi yazdik, deli etmeyin adami.', 1069711200);INSERT INTO ob_sinsi VALUES (21, 'dedikodu arsivi fasilitesi eklenmistir, eski dedikodulari oradan okuyabilirsiniz. ayrica dedikodu fasilitesinin muhtesem suksesi nedeniyle butun dedikodu yazarlarimiza tessekkur ediyor, alninizdan opuyorum.', 1069797600);INSERT INTO ob_sinsi VALUES (22, 'o kim? fasilitesi manyakca yenilendi, cok acayip oldu. ayrica sistemin kullanici hakkinda bilgi tutma dagarcigi kucultuldu, gereksiz olduguna karar verdim. ben kimim\'e tiklayarak farkedebilir, ohannesburg olabilirsiniz.', 1069884000);INSERT INTO ob_sinsi VALUES (23, 'tarih ve saat sistemleri gavur ellerden bir nebze de olsa alinmis, guzellestirilmistir.', 1069884000);INSERT INTO ob_sinsi VALUES (24, 'kim kim? fasilitesi de cilginca restorasyona sokulmus, duzeltilmistir. hayirli olsun.', 1069884000);INSERT INTO ob_sinsi VALUES (25, 'popup fasiliteleri gun boyu suren manyakca bir calismadan sonra duzeltilmistir. eger duzgun cikmiyorlarsa haber ediniz, el sallayiniz, duzelteyim.', 1069970400);INSERT INTO ob_sinsi VALUES (26, 'sevgili yazarlar, o kim? fasilitesinde cikan "en bi sevilen entri" gibi basliklara tiklarsaniz iceriklerini gorebilirsiniz. ayni sekilde bir kez daha tiklarsaniz kapanirlar. maksat sizin parmaklariniz scroll yapmaktan yorulmasin yani, o nedenle.', 1069970400);INSERT INTO ob_sinsi VALUES (27, 'popup sorunlari cozulmustur, daha guzel yarinlara yelken acmamiz dilegiyle (ne dedim ben?).', 1069970400);INSERT INTO ob_sinsi VALUES (28, 'dedikodu fasilitesine degistirme ve silme ozellikleri eklendi. ayrica ana sayfadaki link kalabaligi bir nebze de olsun temizlendi, fasiliteler duzenlendi. aman aman.', 1070056800);INSERT INTO ob_sinsi VALUES (29, 'basliklarda zirtapoz karakterlerin kullanilmasini engelleyen temizlikci teyze fasilitesi devreye girmistir.', 1070056800);INSERT INTO ob_sinsi VALUES (30, 'sevgili yazarlar, artik entri girmek icin sag kolondaki forma basliginizi yazip, yazacam butonuna basabilirsiniz. ayni form alani, tez getür ve getir bobi fasiliteleri icin de kullanilabilir. ahaha!', 1070056800);INSERT INTO ob_sinsi VALUES (31, 'tezyorum fasilitesi eklendi.', 1070056800);INSERT INTO ob_sinsi VALUES (32, 'dedikodu arsivlerine artik kontrol menusundeki dedi linkinden ulasmak mumkun.', 1070056800);INSERT INTO ob_sinsi VALUES (33, 'istek uzerine yoklama fasilitesi eklenmistir, kim kim? linkine basarak gorebilirsiniz. ancak yazarlar sistemden cikmadikca online sayilmaktalar, belki ileride bunu da duzeltirim, su an acil degil.', 1070143200);INSERT INTO ob_sinsi VALUES (34, 'buton yaptim size, yer actim yeni fasiliteler icin.', 1070143200);INSERT INTO ob_sinsi VALUES (35, 'en bi enler fasilitesine en yorumcu yazarlar ve en cok entri girenler istatistikleri eklenmistir, evet evet eklenmistir.', 1070143200);INSERT INTO ob_sinsi VALUES (36, 'yoklama fasilitesindeki hatalar cozulmustur, artik kimlerin online oldugunu rahatlikla gorebilirsiniz dostlar.', 1070229600);INSERT INTO ob_sinsi VALUES (37, 'gorsel degisiklikler yapildi, tezyorum fasilitesi artik sadece hic yorumu olmayan entrilerde cikar oldu (miniac icin), mesaj fasilitesinin temelleri atildi (daha degil), yorum silme fasilitesindeki essek gibi hata duzeltildi (ozur).', 1070229600);INSERT INTO ob_sinsi VALUES (38, 'sevgili yazarlar, eger bir hata gorurseniz, bana mutemadiyen haber edin, ancak hata konusunda entri girmeniz en iyi iletisim sekli olmayabilir, bana mail atin falan. biseyler yapin ama, cunku hata ordaysa buyuk olasilik ben gormedim demektir. ailenizin humanisti.', 1070316000);INSERT INTO ob_sinsi VALUES (39, 'bugun itibariyle mesaj fasilitesi devreye girmistir. kendi capinda hafiften devasa bir fasilite oldugu icin eksikler, hatalar mutlaka cikacaktir. bana mesaj atarak haber edebilirsiniz tabi ki. hehe.', 1067810400);INSERT INTO ob_sinsi VALUES (40, 'sevgili yazarlar, mesaj fasilitesi sistem ici bir mesajlasma fasilitesidir. yani yolladiginiz kisiye email falan atmamaktadir. mesaj yolladiginiz kisi siteye girdigi zaman yukaridaki mesaj butonu yeserecektir, bu sayede mesajinizi oraya tiklayarak okuyabilecektir. evet evet boyle olacaktir. lutfen kullaniniz mutemadiyen. yollarken sadece karsidaki kismin nick\'ini yazmaniz yeterlidir.', 1067810400);INSERT INTO ob_sinsi VALUES (41, 'artik kendi entrileriniz altinda cikan silinsin ozelligiyle silinmesini istediginiz entrileri bana haber verebilirsiniz. ben uygun gorursem silinirler. yorumlari ise hala kendiniz silmeniz mumkun.', 1067896800);INSERT INTO ob_sinsi VALUES (42, 'artik mesaj fasilitesi ana menude kac tane okunmamis mesajiniz oldugunu gosterir oldu. pek guzel oldu.', 1070575200);INSERT INTO ob_sinsi VALUES (43, 'kim kim fasilitesi artik sitedeki uyeleri ayrica gosterebilir oldu. tabi ki butun yazarlari gormek istediginizde onlari da ayrica gorebiliyorsunuz. ne guzel degil mi?', 1071352800);INSERT INTO ob_sinsi VALUES (44, 'incik cincik isimli muthis bir fasilite eklenmis bulunuyor, ancak henuz aktive edilmis degil. yakinda bomba gibi gelecek, aklinizi basinizdan alacak.', 1071352800);INSERT INTO ob_sinsi VALUES (45, 'incik cincik fasilitesi eklendi, sitedeki access violation hatalari duzeltildi. saniyorum server\'in uzun sure restart edilmemesiyle baglantili bir sorundu.', 1068847200);INSERT INTO ob_sinsi VALUES (46, 'orangeblog\'da bahar temizligi yaptim, mis gibi oldu ortalik.', 1082510439);INSERT INTO ob_sinsi VALUES (48, 'bahar temizligi, orangeblog\'un yeni versiyonu. sifirdan tum program yeniden yazildi, cok daha uyumlu, cok daha hizli olmasini umuyorum. kenarda kosede hatalar, eksik bolumler kalmis olabilir, gorurseniz haber edin. hadi bakalim gule gule kullaniniz.', 1082528103);INSERT INTO ob_sinsi VALUES (49, 'starswarm\'un istegi uzerine hazirlanan yeni fasiliteniz, bulusma aparati hayirli ugurlu olsun. artik bulusmalarinizi buradan organize edebilirsiniz arkadaslar.', 1082624156);INSERT INTO ob_sinsi VALUES (50, 'yenü ozelligi eklendi, artik orangeblog\'a ilk girdiginizde sol barda hangi yorumlarin siz sitede yokken yazildigini yanlarina yenü yazarak gosteriyor. bir de bulusmalardaki yeni yorumlari da gosteriyor. ne kadar dusunceliyim degil mi?', 1083176329);INSERT INTO ob_sinsi VALUES (51, 'kalender ozelligini ekledim, artik istediginiz gune zaman yolculugu yapabilirsiniz. ha bi de bulusma zamanlari gunlerin isimlerini gosterir oldu, guzel oldu.', 1083210523);INSERT INTO ob_sinsi VALUES (52, 'vecize ozelligi eklendi. yeni bir vecize girmek icin mutemadiyen zirtapoz bolumune tiklayin, vecize girmek icin zirtapoz girmenize gerek yok, ikisi birbirinden ayri olarak isliyor.', 1083346655);INSERT INTO ob_sinsi VALUES (53, 'elestirmen modu fasilitesi eklendi. artik bloga girdiginiz filmlere oy verebileceksiniz, ayrica film olarak belirlenen entrilerde otomatik imdb baglantisi da cikacak. ne kadar guzel olacak tey tey tey... ha tabi bu ozelligi cikartmak icin sinema kategorisini secmeniz gerekiyor entri girerken.', 1083381343);INSERT INTO ob_sinsi VALUES (54, 'sevgili yazarlar, bakiniz ozelliklerini gelistirmis bulunuyorum, artik sadece (bkz: hebe), (gbkz: hebe) veya (tbkz: hebe) yazarak bakiniz fasilitelerini kullanabilirsiniz. koda moda ihtiyac yok.', 10